package grundlagen;

public class IfElse {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		boolean b = false;
		if (b = false) { 
			System.out.println("Der if-Block ist durchgeführt!");
		}
		else {
			System.out.println("Der else-Block ist durchgeführt!");
		}


	}

}
